mapper.update:
UPDATE `daily_trade_detail`
SET
code = #{code} ,
date = #{date}
,last_end_price = #{lastEndPrice}
,start_price = #{startPrice}
,max_price = #{maxPrice}
,min_price = #{minPrice}
,end_price = #{endPrice}
,trade_volume = #{tradeVolume}
,trade_amt = #{tradeAmt}
,market_value = #{marketValue}
,sc = #{sc}
WHERE
code = #{code} And
date = #{date}

mapper.select:
SELECT

FROM `daily_trade_detail`
WHERE 1=1
 AND code = #{code}
 AND date = #{date}
 AND last_end_price = #{lastEndPrice}
 AND start_price = #{startPrice}
 AND max_price = #{maxPrice}
 AND min_price = #{minPrice}
 AND end_price = #{endPrice}
 AND trade_volume = #{tradeVolume}
 AND trade_amt = #{tradeAmt}
 AND market_value = #{marketValue}
AND sc = #{sc}
